import React, { Component } from 'react';
import Table from 'react-bootstrap/Table';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
class TableView extends React.Component{
    index = new Date().getMonth;
    direction = 0;

    render(){
        {console.log('month: '+index);}

        function ControlledCarousel() {
            const [index, setIndex] = this.useState(0);
            const [direction, setDirection] = this.useState(null);
          
            const handleSelect = (selectedIndex, e) => {
              setIndex(selectedIndex);
              setDirection(e.direction);
            };
        }
        return(
            <Row className="justify-content-md-center" activeIndex={new Date().getMonth} direction={this.direction} onSelect={this.handleSelect}>
                <Col xs={12} md={8}>
                    <Table responsive className="mx-auto text-center">
                        <thead>
                            <tr>
                            <th>Table heading</th>
                            </tr>
                        </thead>
                        <tbody>
                            <tr>
                            <td>Table cell</td>
                            </tr>
                            <tr>
                                <td>ceell1</td>
                            </tr>
                           
                        </tbody>
                    </Table>
                </Col>
            </Row>
        );
    }

}
export default TableView;
